contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
  color: white;
}

img.content-image {
	width: 100%;
  height: auto;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  plugins: [
    "advlist autolink lists link image charmap print",
    "preview anchor searchreplace visualblocks code",
    "fullscreen insertdatetime media table paste"
  ],
  toolbar: "insertfile undo redo | styleselect | bold italic | alignleft aligncenter alignright alignjustify | bullist numlist outdent indent | link image",
  height: 360
});

lessVariables:
@background-color: rgba(51, 58, 64, 1);
@toolbar-background-color: rgba(51, 58, 64, 1);
@menubar-background-color: rgba(51, 58, 64, 1);
@border-color: rgba(51, 58, 64, 1);
@color-black: rgba(255, 255, 255, 1);
@color-white: rgba(47, 45, 45, 1);
@color-tint: #207ab7;
@color-error: #c00;
@color-success: #78AB46;
@base-value: 16px;
@control-border-radius: 3px;
@panel-border-radius: 3px;
@tinymce-border-radius: 0px;
@font-stack: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-text-color: rgba(255, 255, 255, 1);
@toolbar-button-hover-background-color: rgba(32, 31, 31, 1);
@toolbar-button-hover-icon-color: @toolbar-button-icon-color;
@toolbar-button-hover-text-color: @toolbar-button-icon-color;
@toolbar-button-focus-background-color: rgba(255, 255, 255, 1);
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
